extension.valueString:
define "SDE Sex":
  case
      when Patient.gender = 'male' then Code { code: 'M', system: 'http://hl7.org/fhir/v3/AdministrativeGender', display: 'Male' }
      when Patient.gender = 'female' then Code { code: 'F', system: 'http://hl7.org/fhir/v3/AdministrativeGender', display: 'Female' }
      else null
    end

define "Blood Pressure Days":
  (("Qualifying Diastolic Blood Pressure Reading" DBPExam
                                  return date from DBPExam.effective)
                                  intersect ("Qualifying Systolic Blood Pressure Reading" SBPExam
                                  return date from SBPExam.effective))

define "Lowest Diastolic Reading on Most Recent Blood Pressure Day":
  First("Qualifying Diastolic Blood Pressure Reading" DBPReading
                            where DBPReading.effective same day as "Most Recent Blood Pressure Day"
                            return singleton from (
                            DBPReading.component C
                              where C.code ~ "Diastolic blood pressure"
                              return C.value as Quantity
                          )
                    			sort ascending
                    	)

define "Lowest Systolic Reading on Most Recent Blood Pressure Day":
  First("Qualifying Systolic Blood Pressure Reading" SBPReading
                            where SBPReading.effective same day as "Most Recent Blood Pressure Day"
                           return singleton from (
                            SBPReading.component C
                              where C.code ~ "Systolic blood pressure"
                              return C.value as Quantity
                          )
                          sort ascending
                    	)

define "SDE Payer":
  [Coverage: type in "Payer"] Payer
        return {
          code: Payer.type,
          period: Payer.period
        }

define "Has Criteria Indicating Frailty":
  //Ask Bryn about devices
      	exists ( [DeviceRequest: "Frailty Device"] FrailtyDeviceOrder
      			where FrailtyDeviceOrder.authoredOn during "Measurement Period"
      	)
      		/* or exists ( [DeviceUseStatement: "Frailty Device"] FrailtyDeviceUse
      				where Global."Normalize Interval"(FrailtyDeviceUse.timing) overlaps "Measurement Period"
      		) */
      		or exists ( [Condition: "Frailty Diagnosis"] FrailtyDiagnosis
      				where Global."Prevalence Period"(FrailtyDiagnosis) overlaps "Measurement Period"
      		)
      		or exists ( [Encounter: "Frailty Encounter"] FrailtyEncounter
      				where FrailtyEncounter.period overlaps "Measurement Period"
      		)
      		or exists ( [Observation: "Frailty Symptom"] FrailtySymptomObservation
      				where Global."Normalize Interval"(FrailtySymptomObservation.effective) overlaps "Measurement Period"
      		)
